'use client';

import Image from 'next/image'
import { getImageProps } from '../../utils/imageLoader'

const NextImage = ({ src, alt, className, width, height, priority = false, ...props }) => {
  const imageProps = getImageProps(src, alt, className)

  return (
    <Image
      src={src}
      alt={alt}
      width={width}
      height={height}
      className={className}
      priority={priority}
      quality={90}
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
      {...props}
    />
  )
}

export default NextImage 